CREATE OR REPLACE FUNCTION plt_obter_erro_solucao (nr_prescricao_p bigint, nr_seq_solucao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_erro_w		smallint;
ie_liberado_w		varchar(1);

C01 CURSOR FOR
	SELECT	ie_erro
	from	prescr_solucao
	where	nr_prescricao	= nr_prescricao_p
	and	nr_seq_solucao	= nr_seq_solucao_p
	
union

	SELECT	ie_erro
	from	prescr_material
	where	nr_prescricao		= nr_prescricao_p
	and	nr_sequencia_solucao	= nr_seq_solucao_p
	order by 1 desc;


BEGIN

select	plt_obter_se_liberado(dt_liberacao_medico,dt_liberacao,dt_liberacao_farmacia)
into STRICT	ie_liberado_w
from	prescr_medica
where	nr_prescricao	= nr_prescricao_p;

if (ie_liberado_w	= 'N') then

	open C01;
	loop
	fetch C01 into
		ie_erro_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		exit;
		end;
	end loop;
	close C01;

end if;

return	ie_erro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
