CREATE OR REPLACE FUNCTION plt_obter_existe_parcial (nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_existe_w		varchar(1);


BEGIN

select	coalesce(max('S'),'N')
into STRICT	ie_existe_w
from	w_rep_t
where	nm_usuario	= nm_usuario_p
and	dt_atualizacao	between(clock_timestamp() - interval '1 days') and clock_timestamp()
and	ie_estendido	= 'P';

return	ie_existe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
