CREATE OR REPLACE FUNCTION plt_obter_fim_primeiro_plano ( nr_atendimento_p bigint) RETURNS timestamp AS $body$
DECLARE

 
dt_fim_prim_plano_w		timestamp;	
ie_pac_ps_w			varchar(1);				
 

BEGIN 
ie_pac_ps_w	:= 	obter_se_pac_PS(nr_atendimento_p);
 
if (ie_pac_ps_w = 'S') then 
 
  select	max(dt_validade_prescr) 
	into STRICT	dt_fim_prim_plano_w 
	from  	prescr_medica a, 
			setor_atendimento b 
	where	a.nr_atendimento	= nr_atendimento_p     
	and		a.dt_validade_prescr  > clock_timestamp()          
	and		a.cd_setor_atendimento = b.cd_setor_atendimento 
	and		coalesce(a.dt_suspensao::text, '') = '' 
	and		(coalesce(a.dt_liberacao,a.dt_liberacao_medico) IS NOT NULL AND (coalesce(a.dt_liberacao,a.dt_liberacao_medico))::text <> '') 
	and		b.cd_classif_setor	= '1';
	 
else 
 
	select	max(a.dt_validade_prescr) 
	into STRICT	dt_fim_prim_plano_w 
	from	prescr_medica a 
	where	a.nr_prescricao	= (	 
					SELECT	min(b.nr_prescricao) 
					from	prescr_medica b 
					where	b.nr_atendimento	= nr_atendimento_p 
					and	coalesce(b.ie_estendida_liberacao,'N')	= 'N' 
				);
 
end if;
 
if (dt_fim_prim_plano_w - 1 > clock_timestamp()) then 
	dt_fim_prim_plano_w	:= dt_fim_prim_plano_w - 1;
end if;
			 
return	dt_fim_prim_plano_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
