CREATE OR REPLACE FUNCTION plt_obter_ie_copiar_titulo (ie_tipo_item_p text) RETURNS varchar AS $body$
DECLARE


ie_copiar_w		varchar(1);
qt_total_w		bigint;
qt_copiar_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_total_w
from	w_rep_t
where	ie_tipo_item = ie_tipo_item_p
and	coalesce(dt_atualizacao,clock_timestamp()) between(clock_timestamp() - interval '1 days') and (clock_timestamp() + interval '1 days');


select	count(*)
into STRICT	qt_copiar_w
from	w_rep_t
where	ie_tipo_item = ie_tipo_item_p
and	coalesce(dt_atualizacao,clock_timestamp()) between(clock_timestamp() - interval '1 days') and (clock_timestamp() + interval '1 days')
and	coalesce(ie_copiar,'S') = 'S';

if (qt_total_w = qt_copiar_w) then
	ie_copiar_w := 'S';
else
	ie_copiar_w := 'N';
end if;

return	ie_copiar_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
