CREATE OR REPLACE FUNCTION plt_obter_ie_validade ( dt_extensao_p timestamp, dt_validade_prescr_p timestamp, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


/*
ie_validade
S - Estendido
N - Não estendido
P - Parcial (termina antes do horário de corte)
*/
ie_validade_w           varchar(1) := 'N';


BEGIN
if (to_char(dt_validade_prescr_p,'hh24:mi:ss')     <> to_char(dt_referencia_p,'hh24:mi:ss')) and (dt_referencia_p	> dt_validade_prescr_p) then
        ie_validade_w   := 'P';
elsif (dt_extensao_p IS NOT NULL AND dt_extensao_p::text <> '') and (dt_extensao_p  > dt_referencia_p) then
        ie_validade_w   := 'S';
end if;

return  ie_validade_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
