CREATE OR REPLACE FUNCTION plt_obter_inf_setor ( cd_setor_atendimento_p bigint, vl_parametro_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


qt_retorno_w		varchar(255) := null;

qt_dias_estender_w 	 integer := 0;
ie_permite_estender_w	varchar(1);
qt_horas_atraso_extensao_w	integer := 0;
cd_setor_usuario_w		integer;
ie_medicamento_rotina_w	varchar(5);
ie_solucao_rotina_w	varchar(5);
ie_questiona_ext_w		varchar(1);
qt_horas_estender_w	varchar(5);
ds_codigo_prot_sol_w	varchar(255);

-- 'U'  = UsuÃ¡rio
-- 'P'  = Paciente
BEGIN
if (coalesce(vl_parametro_p,0) > 0) then

	cd_setor_usuario_w := SUBSTR(OBTER_SETOR_USUARIO(nm_usuario_p),1,255);

	select	max(coalesce(ie_medicamento_rotina,'N')),
		max(coalesce(ie_solucao_rotina,'N')),
		max(coalesce(ie_questiona_ext,'N')),
		max(coalesce(qt_horas_atraso,0)),
		max(coalesce(qt_dias_estender,0)),
		max(coalesce(ie_permite_estender,'N')),
		max(coalesce(qt_horas_estender,0)),
		max(ds_codigo_prot_sol)
	into STRICT	ie_medicamento_rotina_w,
		ie_solucao_rotina_w,
		ie_questiona_ext_w,
		qt_horas_atraso_extensao_w,
		qt_dias_estender_w,
		ie_permite_estender_w,
		qt_horas_estender_w,
		ds_codigo_prot_sol_w
	from	plt_parametros_setor
	WHERE ((ie_considerar_setor_pac = 'S' AND cd_setor_atendimento = cd_setor_atendimento_p) OR
	       (ie_considerar_setor_pac = 'N' AND cd_setor_atendimento = cd_setor_usuario_w));

	if (vl_parametro_p = 144) and (ie_permite_estender_w = 'S') then
		qt_retorno_w := to_char(qt_dias_estender_w);

	elsif (vl_parametro_p = 16) then
		qt_retorno_w := ie_medicamento_rotina_w;

	elsif (vl_parametro_p = 18) then
		qt_retorno_w := ie_solucao_rotina_w;

	elsif (vl_parametro_p = 44) then
		qt_retorno_w := ie_questiona_ext_w;

	elsif (vl_parametro_p = 78) then
		qt_retorno_w := to_char(coalesce(qt_horas_atraso_extensao_w,0));

	elsif (vl_parametro_p = 73) then
		qt_retorno_w := qt_horas_estender_w;

	elsif (vl_parametro_p = 263) then
		qt_retorno_w :=	ds_codigo_prot_sol_w;

	end if;
end if;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
