SET client_encoding TO 'UTF8';



CREATE TYPE campos AS (nr_prescricao 	double precision);


CREATE OR REPLACE FUNCTION plt_obter_max_nr_prescr_user (nr_prescricoes_p text, cd_prescritor_p text, nm_usuario_p text) RETURNS bigint AS $body$
DECLARE


ds_retorno_w		varchar(20);
nr_prescricoes_w	varchar(4000) := nr_prescricoes_p;
k					smallint;
i					bigint;
nr_max_w			bigint;
nr_prescricao_w		bigint;
ie_prescritor_w		varchar(1);
cd_prescritor_w		pessoa_fisica.cd_pessoa_fisica%type;
type vetor is table of campos index 	by integer;
vetor_w				vetor;

BEGIN

if (coalesce(cd_prescritor_p::text, '') = '') then
	cd_prescritor_w := obter_dados_usuario_opcao(nm_usuario_p, 'C');
else
	cd_prescritor_w := cd_prescritor_p;
end if;

i := 0;
while(length(nr_prescricoes_w) > 0) loop
	begin
	i	:= i+1;
	if (position(',' in nr_prescricoes_w)	>0)  then
		vetor_w[i].nr_prescricao	:= somente_numero(substr(nr_prescricoes_w,1,position(',' in nr_prescricoes_w) ));
		nr_prescricoes_w		:= substr(nr_prescricoes_w,position(',' in nr_prescricoes_w)+1,40000);

	else
		vetor_w[i].nr_prescricao	:= somente_numero(nr_prescricoes_w);
		nr_prescricoes_w		:= null;
	end if;

	end;
end loop;



if (vetor_w.count > 0) then

	FOR y IN REVERSE vetor_w.count..1 loop
		nr_prescricao_w	:= vetor_w[y].nr_prescricao;
		if (nr_max_w	< nr_prescricao_w) or (coalesce(nr_max_w::text, '') = '') then
			if (cd_prescritor_w IS NOT NULL AND cd_prescritor_w::text <> '') then
				select coalesce(max('S'),'N')
				into STRICT	ie_prescritor_w
				from	prescr_medica
				where 	nr_prescricao = nr_prescricao_w
				and	cd_prescritor = cd_prescritor_w;

				if (ie_prescritor_w <> 'N') then
					nr_max_w	:= nr_prescricao_w;
				end if;
			else
				nr_max_w	:= nr_prescricao_w;
			end if;
		end if;
	end loop;

	if (coalesce(nr_max_w::text, '') = '') then
		nr_max_w	:= nr_prescricao_w;
	end if;

end if;

return nr_max_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
