CREATE OR REPLACE FUNCTION plt_obter_nr_seq_leite_deriv ( nr_seq_leite_p bigint, qt_item_p bigint, cd_item_p bigint, cd_intervalo_p text, nr_prescricao_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_leite_deriv_w	bigint;

BEGIN
if (nr_seq_leite_p IS NOT NULL AND nr_seq_leite_p::text <> '') then
	begin
	nr_seq_leite_deriv_w	:= nr_seq_leite_p;
	end;
else
	begin
	select	coalesce(max(nr_seq_leite_deriv),0)
	into STRICT	nr_seq_leite_deriv_w
	from	prescr_material
	where	nr_prescricao	= nr_prescricao_p
	and	coalesce(qt_dose,0)	= coalesce(qt_item_p, coalesce(qt_dose,0))
	and	cd_material	= cd_item_p
	and	coalesce(cd_intervalo,'XPTO') = coalesce(cd_intervalo_p,'XPTO');
	end;
end if;
return	nr_seq_leite_deriv_w;
end;
$body$
