CREATE OR REPLACE FUNCTION plt_obter_ordem_item ( ie_tipo_item_p text, nr_seq_regra_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_apres_w		smallint;


BEGIN

select	coalesce(max(nr_seq_apres),999)
into STRICT	nr_seq_apres_w
from	PLT_regra_ordem_item
where	ie_tipo_item	= ie_tipo_item_p
and	nr_seq_grupo 	= nr_seq_regra_p;

return	nr_seq_apres_w;

end;
