SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION plt_obter_prescricao_item (nr_prescricoes_p text) RETURNS bigint AS $body$
DECLARE


nr_prescricao_w			bigint;
nr_prescricoes_w		varchar(4000);
tam_lista_w			bigint;
ie_pos_virgula_w		smallint;


BEGIN

if (nr_prescricoes_p IS NOT NULL AND nr_prescricoes_p::text <> '') then
	nr_prescricoes_w := nr_prescricoes_p;
	while (nr_prescricoes_w IS NOT NULL AND nr_prescricoes_w::text <> '') loop
		begin
		tam_lista_w		:= length(nr_prescricoes_w);
		ie_pos_virgula_w	:= position(',' in nr_prescricoes_w);
		if (ie_pos_virgula_w <> 0) then
			if (nr_prescricao_w IS NOT NULL AND nr_prescricao_w::text <> '') then
				if ((substr(nr_prescricoes_w,1,(ie_pos_virgula_w - 1)))::numeric  > nr_prescricao_w) then
					nr_prescricao_w		:= (substr(nr_prescricoes_w,1,(ie_pos_virgula_w - 1)))::numeric;
				end if;
			else
				nr_prescricao_w		:= (substr(nr_prescricoes_w,1,(ie_pos_virgula_w - 1)))::numeric;
			nr_prescricoes_w	:= substr(nr_prescricoes_w,(ie_pos_virgula_w + 1),tam_lista_w);
			end if;
		else
			nr_prescricao_w 	:= (nr_prescricoes_w)::numeric;
			nr_prescricoes_w	:= '';
		end if;
		end;
	end loop;
end if;

return	nr_prescricao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
