SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION plt_obter_prescricoes_plano ( ie_opcao_p text, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

/*
ie_opcao_p
T - todas
N - n√£o liberadas
L - liberadas
*/
nr_prescricoes_w		varchar(4000);
ds_prescricoes_w		varchar(1000);
nr_prescricao_w			numeric(20);
tam_lista_w			bigint;
ie_pos_virgula_w		smallint;
nm_usuario_original_w		varchar(15);
ie_emergencia_w			varchar(1);
ie_cartao_emergencia_w		varchar(1);
cd_pessoa_usuario_w		varchar(10);
cd_medico_w			varchar(10);
ie_prescritor_aux_w	varchar(1);

c01 CURSOR FOR
SELECT	nr_prescricoes
from	w_rep_t
where	nm_usuario = nm_usuario_p
and		coalesce(dt_atualizacao,clock_timestamp()) between(clock_timestamp() - interval '1 days') and (clock_timestamp() + interval '1 days')
and	((ie_opcao_p	= 'L' AND ie_liberado	= 'S') or
	 (ie_opcao_p	= 'N' AND ie_liberado	= 'N') or (ie_opcao_p	= 'T'));


BEGIN

select	max(cd_pessoa_fisica)
into STRICT	cd_pessoa_usuario_w
from	usuario
where	nm_usuario	= nm_usuario_p;

open C01;
loop
fetch C01 into
	ds_prescricoes_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	while (ds_prescricoes_w IS NOT NULL AND ds_prescricoes_w::text <> '') loop
		begin
		tam_lista_w		:= length(ds_prescricoes_w);
		ie_pos_virgula_w	:= position(',' in ds_prescricoes_w);
		if (ie_pos_virgula_w <> 0) then
			nr_prescricao_w		:= (substr(ds_prescricoes_w,1,(ie_pos_virgula_w - 1)))::numeric;
			ds_prescricoes_w	:= substr(ds_prescricoes_w,(ie_pos_virgula_w + 1),tam_lista_w);
		else
			nr_prescricao_w		:= (ds_prescricoes_w)::numeric;
			ds_prescricoes_w	:= '';
		end if;

		select	max(nm_usuario_original),
			max(cd_medico),
			max(ie_emergencia),
			max(ie_cartao_emergencia),
			max(ie_prescritor_aux)
		into STRICT	nm_usuario_original_w,
			cd_medico_w,
			ie_emergencia_w,
			ie_cartao_emergencia_w,
			ie_prescritor_aux_w
		from	prescr_medica
		where	nr_prescricao	= nr_prescricao_w;

		if	((nm_usuario_original_w	= nm_usuario_p) or
			 ((cd_medico_w		= cd_pessoa_usuario_w) and
			  ((ie_emergencia_w	= 'S') or (ie_cartao_emergencia_w = 'S') or (ie_prescritor_aux_w = 'S')))) or (ie_opcao_p		<> 'N') then
			nr_prescricoes_w 	:= adep_juntar_prescricao(nr_prescricoes_w, nr_prescricao_w);
		end if;

		end;
	end loop;
	end;
end loop;
close C01;

return	nr_prescricoes_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
