CREATE OR REPLACE FUNCTION plt_obter_prescricoes ( nr_atendimento_p bigint, ie_tipo_item_p text, cd_item_p text, nr_seq_proc_interno_p bigint, cd_intervalo_p text, qt_item_p bigint, dt_validade_p timestamp, nm_usuario_p text, cd_pessoa_fisica_p text, nr_seq_prot_glic_p bigint, hr_prim_horario_p text) RETURNS varchar AS $body$
DECLARE


nr_prescricao_w		bigint;
nr_prescricoes_w	varchar(255) := '';
cd_pessoa_fisica_w	varchar(10);
dt_fim_w		timestamp;

c01 CURSOR FOR
SELECT	a.nr_prescricao
from	prescr_medica a
where	a.nr_atendimento 	= coalesce(nr_atendimento_p,a.nr_atendimento)
and	a.cd_pessoa_fisica	= cd_pessoa_fisica_w
and	obter_se_prescr_vig_adep(a.dt_inicio_prescr,a.dt_validade_prescr,dt_validade_p,dt_fim_w) = 'S'
and	((a.nm_usuario_original	= nm_usuario_p) or (a.dt_liberacao_medico IS NOT NULL AND a.dt_liberacao_medico::text <> ''))
and	exists (
	SELECT	1
	from	prescr_material b
	where	b.nr_prescricao 		= a.nr_prescricao
	and		coalesce(b.dt_suspensao::text, '') = ''
	and	((b.ie_agrupador 		= 1 AND ie_tipo_item_p		= 'M') or
		 (b.ie_agrupador		= 5 AND ie_tipo_item_p		= 'IA'))
	and	b.cd_material 			= cd_item_p
	and	coalesce(b.cd_intervalo,'XPTO')	= coalesce(cd_intervalo_p,'XPTO')
	and	coalesce(b.qt_dose,1) 		= coalesce(qt_item_p,coalesce(b.qt_dose,1))
	and	coalesce(b.hr_prim_horario,'XP')	= coalesce(hr_prim_horario_p,coalesce(b.hr_prim_horario,'XP'))
	)
group by a.nr_prescricao
order by a.nr_prescricao;

c02 CURSOR FOR
SELECT	a.nr_prescricao
from	prescr_medica a
where	a.nr_atendimento 	= coalesce(nr_atendimento_p,a.nr_atendimento)
and	a.cd_pessoa_fisica	= cd_pessoa_fisica_w
