	and	coalesce(b.ie_origem_inf,'XPTO')	<> 'K'
	and	b.cd_material 			= cd_item_p
	and	coalesce(b.cd_intervalo,'XPTO')	= coalesce(cd_intervalo_p,'XPTO')
