BEGIN

dt_fim_w	:= (dt_validade_p + 1) - 1/86400;

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	cd_pessoa_fisica_w	:= coalesce(obter_pessoa_atendimento(nr_atendimento_p,'C'),cd_pessoa_fisica_p);
else
	cd_pessoa_fisica_w	:= cd_pessoa_fisica_p;
end if;
if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') and (ie_tipo_item_p IS NOT NULL AND ie_tipo_item_p::text <> '') and (cd_item_p IS NOT NULL AND cd_item_p::text <> '') and (dt_validade_p IS NOT NULL AND dt_validade_p::text <> '') then
	begin
	if (ie_tipo_item_p in ('M','IA')) then
		begin
		open c01;
		loop
		fetch c01 into nr_prescricao_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			begin
			if (coalesce(nr_prescricoes_w::text, '') = '') then
				nr_prescricoes_w	:= nr_prescricao_w;
			elsif (length(nr_prescricoes_w || ',' || nr_prescricao_w) <= 255 ) then
				nr_prescricoes_w	:= nr_prescricoes_w || ',' || nr_prescricao_w;
			end if;
			end;
		end loop;
		close c01;
		end;
	elsif (ie_tipo_item_p = 'S') then
		begin
		open c02;
		loop
		fetch c02 into nr_prescricao_w;
