		EXIT WHEN NOT FOUND; /* apply on c03 */
			begin
			if (coalesce(nr_prescricoes_w::text, '') = '') then
				nr_prescricoes_w	:= nr_prescricao_w;
			elsif (length(nr_prescricoes_w || ',' || nr_prescricao_w) <= 255 ) then
