CREATE OR REPLACE FUNCTION plt_obter_prescr_nao_lib_ant ( nr_atendimento_p bigint, nm_usuario_p text, dt_quebra_p timestamp, nr_prescricoes_p text) RETURNS varchar AS $body$
DECLARE

nr_prescricao_w		bigint;

BEGIN

select	min(nr_prescricao)
into STRICT	nr_prescricao_w
from	prescr_medica
where	nr_atendimento = nr_atendimento_p
and		nm_usuario = nm_usuario_p
and		coalesce(coalesce(dt_liberacao, dt_liberacao_medico)::text, '') = ''
and		dt_validade_prescr < dt_quebra_p
and		dt_inicio_prescr >= dt_quebra_p - 10
and		obter_se_contido(nr_prescricao,nr_prescricoes_p) = 'N';

return nr_prescricao_w;

end;
