CREATE OR REPLACE FUNCTION plt_obter_prescr_nao_lib ( nr_atendimento_p bigint, nm_usuario_p text, dt_quebra_p timestamp, nr_prescricoes_p text) RETURNS varchar AS $body$
DECLARE

nr_prescricao_w		bigint;

BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (dt_quebra_p IS NOT NULL AND dt_quebra_p::text <> '') and (nr_prescricoes_p IS NOT NULL AND nr_prescricoes_p::text <> '') then
	select	max(nr_prescricao)
