SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION plt_obter_regra_usuario ( nm_usuario_p text, cd_perfil_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_regra_w			bigint;


BEGIN

select	coalesce(max(nr_seq_regra),0)
into STRICT	nr_seq_regra_w
from	plt_regra_lib_item
where	nm_usuario_filtro = nm_usuario_p;

if (nr_seq_regra_w = 0) then

	select	coalesce(max(nr_seq_regra),0)
	into STRICT	nr_seq_regra_w
	from	plt_regra_lib_item
	where	cd_perfil = cd_perfil_p;

end if;

if (nr_seq_regra_w = 0) then

	select	coalesce(max(a.nr_sequencia),0)
	into STRICT	nr_seq_regra_w
	from	plt_regra_ordem_grupo a
	where	not exists (SELECT 1 from plt_regra_lib_item b where a.nr_sequencia = b.nr_seq_regra);

end if;

if (nr_seq_regra_w = 0) then

	select	coalesce(max(nr_seq_regra),0)
	into STRICT	nr_seq_regra_w
	from	plt_regra_lib_item
	where	coalesce(cd_perfil::text, '') = ''
	and	coalesce(nm_usuario_filtro::text, '') = '';

end if;

return	nr_seq_regra_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
