CREATE OR REPLACE FUNCTION plt_obter_se_estendido (dt_extensao_p timestamp, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_estendido_w		varchar(1) := 'N';


BEGIN

if (dt_extensao_p IS NOT NULL AND dt_extensao_p::text <> '') and (dt_extensao_p	> dt_referencia_p) then
	ie_estendido_w	:= 'S';
end if;

return	ie_estendido_w;

end;
