CREATE OR REPLACE FUNCTION plt_obter_se_exibe_inc ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_exibe_w			varchar(10)	:= 'N';
nr_prescricao_w			bigint;
nr_seq_item_w			bigint;
ie_tipo_item_w			varchar(15);
nm_usuario_w			varchar(15);
nr_seq_exibe_w			bigint;


BEGIN
select	max(nr_prescricao),
	max(nr_seq_item),
	max(ie_tipo_item),
	max(nm_usuario)
into STRICT	nr_prescricao_w,
	nr_seq_item_w,
	ie_tipo_item_w,
	nm_usuario_w
from	w_copia_plano
where	nr_sequencia	= nr_sequencia_p;

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_exibe_w
from	w_copia_plano
where	nr_prescricao	= nr_prescricao_w
and	nr_seq_item	= nr_seq_item_w
and	ie_tipo_item	= ie_tipo_item_w
and	nm_usuario	= nm_usuario_w
and	ie_permite	= 'N';

if (nr_seq_exibe_w = 0) then

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_exibe_w
	from	w_copia_plano
	where	nr_prescricao	= nr_prescricao_w
	and	nr_seq_item	= nr_seq_item_w
	and	ie_tipo_item	= ie_tipo_item_w
	and	nm_usuario	= nm_usuario_w;

end if;

if (nr_sequencia_p	= nr_seq_exibe_w) then
	ie_exibe_w := 'S';
end if;

return	ie_exibe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
