CREATE OR REPLACE FUNCTION plt_obter_se_exibe_item ( ie_tipo_item_p text, nr_seq_regra_p text) RETURNS varchar AS $body$
DECLARE


ie_exibe_w		varchar(1);


BEGIN

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_exibe_w
from	plt_regra_ordem_item
