CREATE OR REPLACE FUNCTION plt_obter_se_mostra_rep (ie_motivo_prescricao_p text) RETURNS varchar AS $body$
DECLARE


ie_mostrar_w	varchar(10);


BEGIN

select	coalesce(max(ie_visualizar),'S')
into STRICT	ie_mostrar_w
from	PLT_VISUALIZACAO_PRESCR
where	coalesce(ie_motivo_prescricao,coalesce(ie_motivo_prescricao_p,'XPTO'))	= coalesce(ie_motivo_prescricao_p,'XPTO');

return	ie_mostrar_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
