CREATE OR REPLACE FUNCTION plt_obter_se_plano_atual ( nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_atual_w		varchar(1);
dt_inicio_w		timestamp;


BEGIN

select	max(dt_validade_prescr)
into STRICT	dt_inicio_w
from	prescr_medica
where	nr_prescricao	= nr_prescricao_p;

dt_inicio_w	:= ((dt_inicio_w	- 1) + 1/86400);

select	coalesce(max('S'),'N')
into STRICT	ie_atual_w
from	prescr_medica
where	nr_prescricao	= nr_prescricao_p
and	clock_timestamp()		between dt_inicio_w and dt_validade_prescr;

return	ie_atual_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
