SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION plt_obter_se_plano_dieta ( dt_quebra_p timestamp, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_prescr_dieta_w		varchar(1);
dt_inicio_w			timestamp := (dt_quebra_p - 1);


BEGIN

select	coalesce(max('S'),'N')
into STRICT	ie_prescr_dieta_w
from (	SELECT	1
		from	prescr_dieta a,
			prescr_medica b
		where	a.nr_prescricao		= b.nr_prescricao
		and	b.nr_atendimento	= nr_atendimento_p
		and	obter_se_prescr_vig_adep(b.dt_inicio_prescr,b.dt_validade_prescr,dt_inicio_w,dt_quebra_p) = 'S'
		and	coalesce(a.dt_suspensao::text, '') = ''
		and	coalesce(b.dt_suspensao::text, '') = ''
		
union all

		SELECT	1
		from	prescr_leite_deriv a,
			prescr_medica b
		where	a.nr_prescricao 	= b.nr_prescricao
		and	b.nr_atendimento	= nr_atendimento_p
		and	obter_se_prescr_vig_adep(b.dt_inicio_prescr,b.dt_validade_prescr,dt_inicio_w,dt_quebra_p) = 'S'
		and	coalesce(b.dt_suspensao::text, '') = ''
		
union all

		select	1
		from	prescr_material a,
			prescr_medica b
		where	a.nr_prescricao 	= b.nr_prescricao
		and	b.nr_atendimento	= nr_atendimento_p
		and 	a.ie_agrupador 		in (8,12,16,17)
		and	coalesce(a.dt_suspensao::text, '') = ''
		and	coalesce(b.dt_suspensao::text, '') = ''
		and	obter_se_prescr_vig_adep(b.dt_inicio_prescr,b.dt_validade_prescr,dt_inicio_w,dt_quebra_p) = 'S'
		
union all

		select	1
		from	rep_jejum a,
			prescr_medica b
		where	a.nr_prescricao 	= b.nr_prescricao
		and	b.nr_atendimento	= nr_atendimento_p
		and	coalesce(b.dt_suspensao::text, '') = ''
		and	coalesce(a.ie_suspenso,'N')	<> 'S'
		and	obter_se_prescr_vig_adep(b.dt_inicio_prescr,b.dt_validade_prescr,dt_inicio_w,dt_quebra_p) = 'S'
		) alias14;

return	ie_prescr_dieta_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
