CREATE OR REPLACE FUNCTION plt_obter_se_prescr_plt_atual ( nr_prescricao_p bigint, dt_quebra_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_atual_w		varchar(1);


BEGIN

select	coalesce(max('S'),'N')
into STRICT	ie_atual_w
from	prescr_medica
where	nr_prescricao		= nr_prescricao_p
and	dt_validade_prescr	= (dt_quebra_p  - 1/86400);

return	ie_atual_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
