CREATE OR REPLACE FUNCTION plt_obter_se_sae_plt_atual ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_atual_w		varchar(1);


BEGIN

select	coalesce(max('S'),'N')
into STRICT	ie_atual_w
from	pe_prescricao
where	nr_sequencia	= nr_sequencia_p
and	clock_timestamp()		between dt_inicio_prescr and dt_validade_prescr;

