CREATE OR REPLACE FUNCTION plt_ordenar_prescricoes ( ds_lista_p text, ie_tipo_p text, ie_horarios_dietas_orais_p text) RETURNS varchar AS $body$
DECLARE


ds_lista_w		varchar(4000);
ds_prescricao_w		varchar(255);
nr_prescricao_w		numeric(20);
nr_sequencia_w		numeric(20);

c01 CURSOR FOR
SELECT	distinct nr_prescricao||' '||nr_sequencia||',',
	nr_prescricao,
	nr_sequencia
from	prescr_material
where (position(','||nr_prescricao||' '||nr_sequencia||','  ','||ds_lista_p) > 0)
order by nr_prescricao, nr_sequencia;

c02 CURSOR FOR
SELECT	distinct nr_prescricao||' '||nr_sequencia||',',
	nr_prescricao,
	nr_sequencia
from	prescr_procedimento
where (position(','||nr_prescricao||' '||nr_sequencia||','  ','||ds_lista_p) > 0)
order by nr_prescricao, nr_sequencia;

c03 CURSOR FOR
SELECT	distinct CASE WHEN ie_horarios_dietas_orais_p='S' THEN 				a.nr_prescricao||' '||a.nr_sequencia||';'||coalesce(b.cd_refeicao,a.cd_dieta)||','  ELSE a.nr_prescricao||' '||a.nr_sequencia||';'||a.cd_dieta||',' END ,
	a.nr_prescricao,
	a.nr_sequencia
from	prescr_dieta_hor b,
	prescr_dieta a
where	(((position(','||a.nr_prescricao||' '||a.nr_sequencia||';'||coalesce(b.cd_refeicao,a.cd_dieta)||',' in ','||ds_lista_p) > 0) and (ie_horarios_dietas_orais_p = 'S')) or
	 ((position(','||a.nr_prescricao||' '||a.nr_sequencia||';'||a.cd_dieta||',' in ','||ds_lista_p) > 0) and (ie_horarios_dietas_orais_p = 'N')))
and	a.nr_prescricao = b.nr_prescricao
and	a.nr_sequencia = b.nr_seq_dieta
order by a.nr_prescricao, a.nr_sequencia;


BEGIN

if (ie_tipo_p = 'M') then
	open C01;
	loop
	fetch C01 into
		ds_prescricao_w,
		nr_prescricao_w,
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		if ( coalesce(ds_lista_w::text, '') = '') then
			ds_lista_w := ds_prescricao_w;
		else
			ds_lista_w := ds_lista_w||ds_prescricao_w;
		end if;
		end;
	end loop;
	close C01;
elsif (ie_tipo_p = 'P') then
	open C02;
	loop
	fetch C02 into
		ds_prescricao_w,
		nr_prescricao_w,
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		if ( coalesce(ds_lista_w::text, '') = '') then
			ds_lista_w := ds_prescricao_w;
		else
			ds_lista_w := ds_lista_w||ds_prescricao_w;
		end if;
		end;
	end loop;
	close C02;
elsif (ie_tipo_p = 'D') then
	open C03;
	loop
	fetch C03 into
		ds_prescricao_w,
		nr_prescricao_w,
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on C03 */
		begin
		if ( coalesce(ds_lista_w::text, '') = '') then
			ds_lista_w := ds_prescricao_w;
		else
			ds_lista_w := ds_lista_w||ds_prescricao_w;
		end if;
		end;
	end loop;
	close C03;
end if;

return	ds_lista_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
