CREATE OR REPLACE FUNCTION pmo_obter_nome_programa (cd_projeto_p bigint, ds_programa_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

if (cd_projeto_p IS NOT NULL AND cd_projeto_p::text <> '') then

	select 	ds_titulo
	into STRICT	ds_retorno_w
	from 	proj_projeto
	where 	nr_sequencia = cd_projeto_p;

else
	ds_retorno_w := ds_programa_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
