CREATE OR REPLACE FUNCTION pmo_spi_hpi_status ( spi_p bigint, hpi_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(10);


BEGIN

	select	case
			when(spi_p < 0.8 or hpi_p < 0.8)
				then '4' -- Delayed
			when(spi_p >= 0.8 and spi_p < 0.9
				and hpi_p >= 0.8 and hpi_p < 0.9)
				then '3' -- Risk
			else	'1' -- In progress
		end
	into STRICT	ds_retorno_w
	;

	return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
