CREATE OR REPLACE FUNCTION pont_tria_escala_man ( qt_peso_p bigint, qt_altura_p bigint, ie_stress_p text, ie_perda_peso_p text, ie_mobilidade_p text, ie_neurologico_p text, ie_declineo_ingestao_p text) RETURNS bigint AS $body$
DECLARE


qt_imc_w				double precision;
qt_pontuacao_triagem_w	integer := 0;


BEGIN

qt_imc_w	:= dividir(qt_peso_p,(qt_altura_p /100) * (qt_altura_p /100));

if (qt_imc_w >= 19) and (qt_imc_w < 21) then
	qt_pontuacao_triagem_w	:= qt_pontuacao_triagem_w + 1;
elsif (qt_imc_w >= 21) and (qt_imc_w < 23) then
	qt_pontuacao_triagem_w	:= qt_pontuacao_triagem_w + 2;
elsif (qt_imc_w >= 23) then
	qt_pontuacao_triagem_w	:= qt_pontuacao_triagem_w + 3;

end if;

qt_imc_w := qt_imc_w;

if (ie_stress_p = 'N') then
	qt_pontuacao_triagem_w	:= qt_pontuacao_triagem_w + 2;
end if;

qt_pontuacao_triagem_w	:= qt_pontuacao_triagem_w + (ie_perda_peso_p)::numeric;
qt_pontuacao_triagem_w	:= qt_pontuacao_triagem_w + (ie_mobilidade_p)::numeric;
qt_pontuacao_triagem_w	:= qt_pontuacao_triagem_w + (ie_neurologico_p)::numeric;
qt_pontuacao_triagem_w	:= qt_pontuacao_triagem_w + (ie_declineo_ingestao_p)::numeric;

return	qt_pontuacao_triagem_w;

end;
