CREATE OR REPLACE FUNCTION possui_regra_tipo_cons_oft ( nr_seq_consulta_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ie_possui_w		varchar(1)  := 'N';
nm_usuario_w		varchar(15) := Wheb_usuario_pck.get_nm_usuario;
cd_perfil_w		bigint   := wheb_usuario_pck.get_cd_perfil;
qt_regra_w		integer;
nr_seq_tipo_consulta_w	bigint;


BEGIN

if (nr_seq_consulta_p IS NOT NULL AND nr_seq_consulta_p::text <> '') then
	select max(nr_seq_tipo_consulta)
	into STRICT   nr_seq_tipo_consulta_w
	from   oft_consulta
	where  nr_sequencia =  nr_seq_consulta_p;

	select count(*)
	into STRICT   qt_regra_w
	from   oft_tipo_consulta_item
	where  nr_seq_tipo_consulta = nr_seq_tipo_consulta_w;

	if (qt_regra_w > 0) then
		ie_possui_w := 'S';

	else
		ie_possui_w := 'N';
	end if;
end if;

return	ie_possui_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
