CREATE OR REPLACE FUNCTION possui_regra_visuali_estab (cd_estabelecimento bigint, cd_estabelecimento_atual bigint, IE_RESTRINGE_ESTAB_P text) RETURNS varchar AS $body$
DECLARE

  ie_permite_visualizar varchar(1);
  ie_possui_regra varchar(1);


BEGIN

  ie_permite_visualizar := 'N';
  ie_possui_regra       := 'N';

  if (IE_RESTRINGE_ESTAB_P = 'N' or
     cd_estabelecimento = cd_estabelecimento_atual) then
    ie_permite_visualizar := 'S';

   else
     select CASE WHEN b.registros=0 THEN  'N'  ELSE 'S' END
      into STRICT ie_possui_regra
      from (SELECT count(*) registros

              from san_regra_visual_estab a

             where a.cd_estabelecimento = 7

               and a.cd_estab_regra = 1

               and a.ie_situacao = 'A') b;
  end if;

  if (IE_RESTRINGE_ESTAB_P = 'R' and ie_possui_regra = 'S') then
    ie_permite_visualizar := 'S';
  end if;

  return ie_permite_visualizar;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
