SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION ppm_obter_se_mes_fechado (nr_seq_resultado_p bigint) RETURNS varchar AS $body$
DECLARE


dt_referencia_w		timestamp;


BEGIN

select	max(dt_referencia)
into STRICT	dt_referencia_w
from	ppm_objetivo_result
where	nr_sequencia	= nr_seq_resultado_p;

if (trunc(PKG_DATE_UTILS.end_of(dt_referencia_w,'MONTH')) < trunc(clock_timestamp())) then
	return 'S';
/* Acumulado foi definido como 31/12 23:59 */

elsif (dt_referencia_w = PKG_DATE_UTILS.end_of(to_date('31/12/' || to_char(dt_referencia_w,'yyyy'),'dd/mm/yyyy'),'DAY')) then
	return 'S';
else
	return 'N';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
