CREATE OR REPLACE FUNCTION ppm_obter_user_gestor (nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


nm_usuario_w            varchar(15);
cd_pessoa_fisica_w      varchar(10);


BEGIN

select  sis_obter_gerente(nm_usuario_p,'C')
into STRICT    cd_pessoa_fisica_w
;

select  max(nm_usuario)
into STRICT    nm_usuario_w
from    usuario
where   cd_pessoa_fisica = cd_pessoa_fisica_w
and     ie_situacao = 'A';

return  nm_usuario_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
