SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION ppm_obter_user_resp_depto (nr_seq_result_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_departamento_w	bigint;
nr_seq_gerencia_w	bigint;
nr_seq_grupo_w		bigint;
ds_retorno_w		varchar(20);


BEGIN

select	max(a.nr_seq_departamento),
	max(a.nr_seq_gerencia),
	max(a.nr_seq_grupo)
into STRICT	nr_seq_departamento_w,
	nr_seq_gerencia_w,
	nr_seq_grupo_w
from	ppm_objetivo a,
	ppm_objetivo_meta b,
	ppm_objetivo_metrica c,
	ppm_objetivo_result d
where	a.nr_sequencia	= b.nr_seq_objetivo
and	b.nr_sequencia	= c.nr_seq_meta
and	c.nr_sequencia	= d.nr_seq_metrica
and	d.nr_sequencia	= nr_seq_result_p;

ds_retorno_w := 'N';

if (nr_seq_grupo_w IS NOT NULL AND nr_seq_grupo_w::text <> '') then

	begin
	select	nm_usuario_lider
	into STRICT	ds_retorno_w
	from	GERENCIA_WHEB_GRUPO
	where	nr_sequencia		= nr_seq_grupo_w  LIMIT 1;
	exception
	when others then
		ds_retorno_w	:= null;
	end;

elsif (nr_seq_gerencia_w IS NOT NULL AND nr_seq_gerencia_w::text <> '') then

	begin
	select	b.nm_usuario
	into STRICT	ds_retorno_w
	from	GERENCIA_WHEB a,
		usuario b
	where	a.nr_sequencia		= nr_seq_gerencia_w
	and	a.CD_RESPONSAVEL	= b.cd_pessoa_fisica  LIMIT 1;
	exception
	when others then
		ds_retorno_w	:= null;
	end;

elsif (nr_seq_departamento_w IS NOT NULL AND nr_seq_departamento_w::text <> '') then

	begin
	select	b.nm_usuario
	into STRICT	ds_retorno_w
	from	DEPARTAMENTO_PHILIPS a,
		usuario b
	where	a.nr_sequencia		= nr_seq_departamento_w
	and	a.CD_PESSOA_FISICA	= b.cd_pessoa_fisica  LIMIT 1;
	exception
	when others then
		ds_retorno_w	:= 'N';
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION ppm_obter_user_resp_depto (nr_seq_result_p bigint) FROM PUBLIC;

