CREATE OR REPLACE FUNCTION ppm_satisfacao_acumulada (dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


dt_inicial_w		timestamp;
dt_final_w			timestamp;
pr_satisfacao_w		double precision;


BEGIN

dt_inicial_w	:= PKG_DATE_UTILS.start_of(dt_referencia_p, 'YEAR', 0);
dt_final_w		:= PKG_DATE_UTILS.ADD_MONTH(PKG_DATE_UTILS.start_of(dt_referencia_p, 'YEAR', 0)-1,12, 0);


select	(dividir((
select	count(*)
	from	man_ordem_servico a
	where	PKG_DATE_UTILS.start_of(a.dt_fim_real, 'dd', 0) between dt_inicial_w and dt_final_w
	and	a.ie_grau_satisfacao in ('B','O','E')
	and	a.ie_status_ordem = 3
	and	coalesce(a.nr_seq_meta_pe::text, '') = ''
	and	coalesce(a.nr_seq_obj_bsc::text, '') = ''
	and	exists	(select	1
		 from	man_ordem_serv_estagio y,
			man_estagio_processo x
		 where	x.nr_sequencia	= y.nr_seq_estagio
		 and	y.nr_seq_ordem	= a.nr_sequencia
		 and	((x.ie_desenv	= 'S') or (x.ie_tecnologia = 'S'))) ),
		 (
     select count(*)
		FROM man_ordem_servico a
LEFT OUTER JOIN man_grau_satisf_justif b ON (a.nr_seq_justif_grau_satisf = b.nr_sequencia)
WHERE coalesce(b.ie_indicador,'S') = 'S' and PKG_DATE_UTILS.start_of(a.dt_fim_real, 'dd', 0) between dt_inicial_w and dt_final_w and coalesce(a.ie_grau_satisfacao, 'N') <> 'N' and a.ie_status_ordem = 3 and coalesce(a.nr_seq_meta_pe::text, '') = '' and coalesce(a.nr_seq_obj_bsc::text, '') = '' and exists	(select	1
				from	man_ordem_serv_estagio y,
					man_estagio_processo x
				where	x.nr_sequencia	= y.nr_seq_estagio
				and	y.nr_seq_ordem	= a.nr_sequencia
			and	((x.ie_desenv	= 'S') or (x.ie_tecnologia = 'S'))) )) * 100)
into STRICT	pr_satisfacao_w
;

return	pr_satisfacao_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
