SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION prof_obter_datas_orcamento (nr_seq_projeto_p bigint) RETURNS varchar AS $body$
DECLARE


dt_orcamento_w		timestamp;
ds_datas_w		varchar(4000);

C01 CURSOR FOR
	SELECT	dt_orcamento
	from	proj_orcamento
	where	nr_seq_proj	= nr_seq_projeto_p
	and	ie_situacao	= 'A'
	order by dt_orcamento;


BEGIN
open C01;
loop
fetch C01 into
	dt_orcamento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_datas_w	:= ds_datas_w ||', '|| to_char(dt_orcamento_w,'dd/mm/yyyy');
	end;
end loop;
close C01;

return	substr(ds_datas_w,3,4000);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
