CREATE OR REPLACE FUNCTION programas_saude_em_dia (nr_seq_programa_partic_p bigint, nr_programa_p bigint) RETURNS varchar AS $body$
DECLARE

				
ie_opcao_w		varchar(5) := 'S';
nr_qtd_w		bigint;
	

BEGIN
	SELECT COUNT(1)
	INTO STRICT nr_qtd_w
	FROM mprev_partic_ciclo_item_v
	WHERE nr_seq_participante = nr_seq_programa_partic_p
	  AND nr_seq_programa = nr_programa_p
	  AND ie_status IN ('P', 'A')
	  AND TRUNC(dt_prevista) < TRUNC(clock_timestamp());

	if (nr_qtd_w > 0) then
		ie_opcao_w := 'N';
	end if;

	return obter_expressao_sim_nao(ie_opcao_w);
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
