CREATE OR REPLACE FUNCTION proj_calc_perc_real (nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


qt_retorno_w		double precision := 0;
qt_fases_w		bigint;
nr_seq_cron_etapa_w	bigint;
qt_calculo_w		bigint := 0;

c01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	proj_cron_etapa a
	where	a.nr_seq_superior = nr_sequencia_p
	and	a.ie_fase = 'S'
	and	coalesce(a.ie_situacao,'A') = 'A';


BEGIN

select	count(*)
into STRICT	qt_fases_w
from	proj_cron_etapa
where	nr_seq_superior = nr_sequencia_p
and	ie_fase = 'S'
and	coalesce(ie_situacao,'A') = 'A';

if (qt_fases_w = 0) then
	begin
	select	coalesce(sum(dividir(((CASE WHEN a.ie_fase='S' THEN  dividir((a.qt_hora_real * 100), a.qt_hora_prev) WHEN a.ie_fase='N' THEN a.pr_etapa END ) *
		proj_calc_horas_prev(a.nr_sequencia,a.nr_seq_superior)),100)),0)
	into STRICT	qt_retorno_w
	from	proj_cron_etapa a
	where	a.nr_seq_superior = nr_sequencia_p
	and	coalesce(a.ie_situacao,'A') = 'A';
	end;
else
	begin
	open C01;
	loop
	fetch C01 into
		nr_seq_cron_etapa_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		select	coalesce(sum(dividir(((CASE WHEN a.ie_fase='S' THEN  dividir((a.qt_hora_real * 100), a.qt_hora_prev) WHEN a.ie_fase='N' THEN a.pr_etapa END ) * proj_calc_horas_prev(a.nr_sequencia,coalesce(a.nr_seq_superior,a.nr_sequencia))),100)),0)
		into STRICT	qt_calculo_w
		from	proj_cron_etapa a
		where	a.nr_sequencia = nr_seq_cron_etapa_w;

		qt_retorno_w	:= qt_retorno_w + qt_calculo_w;
		end;
	end loop;
	close C01;

	end;
end if;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
