CREATE OR REPLACE FUNCTION proj_capitalizacao_dados_per ( nr_seq_projeto_p bigint, nm_usuario_recurso_p text, dt_inicial_p timestamp, dt_final_p timestamp, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/* ie_opcao_p

HEM	- horas executadas mês
HA	- horas acumuladas
PEP	- possui execução de alguma os no período

HEMR	- horas executadas mês recurso
HAR	- horas acumuladas recurso
*/
qt_ordem_serv_w	bigint;
qt_minutos_w	double precision;

qt_retorno_w	double precision;


BEGIN

select	sum(qt_ordem_serv) qt_ordem_serv,
	sum(qt_minutos) qt_minutos
into STRICT	qt_ordem_serv_w,
	qt_minutos_w
from (	SELECT	sum(v.qt_minuto) qt_minutos,
		count(m.nr_sequencia) qt_ordem_serv
	from 	proj_projeto a,
		proj_cronograma p,
		proj_cron_etapa e,
		man_ordem_servico m,
		man_ordem_serv_ativ v
	where	a.nr_sequencia = p.nr_seq_proj
	and	p.nr_sequencia = e.nr_seq_cronograma
	and	e.nr_sequencia = m.nr_seq_proj_cron_etapa
	and	m.nr_sequencia = v.nr_seq_ordem_serv
	and	((p.ie_classificacao <> 'N') or (coalesce(p.ie_classificacao::text, '') = ''))
	and	a.nr_sequencia = nr_seq_projeto_p
	and	((ie_opcao_p = 'HA') or (trunc(v.dt_atividade, 'dd') between dt_inicial_p and dt_final_p and ie_opcao_p = 'HEM') or (ie_opcao_p = 'HAR' and v.nm_usuario = nm_usuario_recurso_p) or (trunc(v.dt_atividade, 'dd') between dt_inicial_p and dt_final_p and
		  v.nm_usuario = nm_usuario_recurso_p and ie_opcao_p = 'HEMR'))
	
union

	SELECT	sum(v.qt_minuto) qt_minutos,
		count(m.nr_sequencia) qt_ordem_serv
	from 	proj_projeto a,
		man_ordem_servico m,
		man_ordem_serv_ativ v
	where	a.nr_seq_ordem_serv = m.nr_sequencia
	and	m.nr_sequencia = v.nr_seq_ordem_serv
	and	a.nr_sequencia = nr_seq_projeto_p
	and	((ie_opcao_p = 'HA') or (trunc(v.dt_atividade, 'dd') between dt_inicial_p and dt_final_p and ie_opcao_p = 'HEM') or (ie_opcao_p = 'HAR' and v.nm_usuario = nm_usuario_recurso_p) or (trunc(v.dt_atividade, 'dd') between dt_inicial_p and dt_final_p and
		  v.nm_usuario = nm_usuario_recurso_p and ie_opcao_p = 'HEMR'))) alias24;

if (ie_opcao_p in ('HA','HEM','HAR','HEMR')) then
	qt_retorno_w := coalesce(qt_minutos_w,0);
else
	qt_retorno_w := coalesce(qt_ordem_serv_w,0);
end if;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
