CREATE OR REPLACE FUNCTION proj_des_obter_programador ( nr_seq_projeto_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w	varchar(255) := '';				
				 

BEGIN 
if (nr_seq_projeto_p IS NOT NULL AND nr_seq_projeto_p::text <> '') then 
	begin 
	select	max(obter_nome_pf(a.cd_pessoa_fisica)) 
	into STRICT	ds_retorno_w 
	from	proj_equipe_papel a, 
		proj_equipe b 
	where	a.nr_seq_equipe = b.nr_sequencia 
	and	a.nr_seq_funcao	= 44 
	and	b.ie_interna_externa = 'I' 
	and	b.nr_seq_proj = nr_seq_projeto_p 
	and	coalesce(a.ie_situacao,'A') = 'A' 
	and	coalesce(a.ie_funcao_rec_migr,'M') = 'M';
	end;
end if;
return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
