CREATE OR REPLACE FUNCTION proj_dias_prev_etapa_npi ( qt_hora_prev_p bigint) RETURNS bigint AS $body$
DECLARE


qt_retorno_w		double precision;
qt_horas_w		smallint := 7; -- Considerando 1 dia = 7 horas
BEGIN
if (coalesce(qt_hora_prev_p,0) > 0) then
	begin
	qt_retorno_w := ((qt_hora_prev_p/qt_horas_w) - trunc(qt_hora_prev_p/qt_horas_w));
	if (qt_retorno_w = 0) then
		qt_retorno_w := trunc(qt_hora_prev_p/qt_horas_w);
	else	qt_retorno_w := trunc(qt_hora_prev_p/qt_horas_w)+1;
	end if;
	end;
elsif (coalesce(qt_hora_prev_p,0) = 0) then
	qt_retorno_w := 0;
end if;

return	qt_retorno_w;

end;
$body$
