CREATE OR REPLACE FUNCTION proj_obter_conhecimento_nivel (nr_seq_nivel_p bigint) RETURNS bigint AS $body$
DECLARE


pr_conhecimento_w	double precision := 0;
pr_conhecimento_total_w	double precision;
cd_consultor_w		bigint;
total_consultor_w	bigint := 0;


C01 CURSOR FOR
SELECT	c.nr_sequencia
from (select	z.cd_consultor,
	 max(z.dt_atualizacao) dt_atualizacao
	 from 	proj_consultor_nivel z
	 group by z.cd_consultor) a,
	proj_consultor_nivel b,
	com_canal_consultor c
where	a.dt_atualizacao = b.dt_atualizacao
and	b.cd_consultor = c.cd_pessoa_fisica
and	b.nr_seq_nivel = nr_seq_nivel_p
and	c.ie_situacao = 'A'
and	c.ie_regime_contr = 'CLT'
and	c.ie_consultor_wheb = 'S';


BEGIN

open C01;
loop
fetch C01 into
	cd_consultor_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	sum(pr_conhecimento)/coalesce(CASE WHEN count(*)=0 THEN 1  ELSE count(*) END ,1)
	into STRICT	pr_conhecimento_total_w
	from	com_cons_gest_con_mod a,
		com_cons_gest_con_fun b
	where	a.nr_sequencia = b.nr_seq_conhecimento
	and	a.nr_seq_consultor = cd_consultor_w;

	pr_conhecimento_w := pr_conhecimento_w + pr_conhecimento_total_w;
	total_consultor_w := total_consultor_w + 1;

	end;
end loop;
close C01;

if total_consultor_w = 0 then
	total_consultor_w := 1;
end if;
pr_conhecimento_w := round((pr_conhecimento_w/total_consultor_w)::numeric,2);

return	pr_conhecimento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
