SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION proj_obter_dados_aval_pf ( cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*	ie_opcao_p
P	dt_prox_avaliacao
A	dt_avaliacao */
nr_sequencia_w		bigint;
ds_retorno_w		varchar(255) := '';
dt_prox_avaliacao_w	timestamp;
dt_avaliacao_w		timestamp;


BEGIN
select	max(x.nr_sequencia)
into STRICT	nr_sequencia_w
from	pessoa_fisica_avaliacao x
where	x.cd_pessoa_fisica = cd_pessoa_fisica_p;

select	max(a.dt_prox_avaliacao),
	max(a.dt_avaliacao)
into STRICT	dt_prox_avaliacao_w,
	dt_avaliacao_w
from	pessoa_fisica_avaliacao a
where	a.nr_sequencia = nr_sequencia_w;

if (coalesce(ie_opcao_p,'X') = 'P') then
	ds_retorno_w := to_char(dt_prox_avaliacao_w,'dd/mm/yyyy');
elsif (coalesce(ie_opcao_p,'X') = 'A') then
	ds_retorno_w := to_char(dt_avaliacao_w,'dd/mm/yyyy');
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
