CREATE OR REPLACE FUNCTION proj_obter_dados_consultor ( cd_consultor_p text, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*
ie_opcao_p
NR - sequencia do consultor
*/
ds_retorno_w	bigint;
nr_consultor_w	bigint;


BEGIN
select	a.nr_sequencia
into STRICT	nr_consultor_w
from	pessoa_fisica b,
		com_canal_consultor a
where	a.cd_pessoa_fisica = b.cd_pessoa_fisica
and		a.ie_situacao = 'A'
and		a.cd_pessoa_fisica = cd_consultor_p;

if (ie_opcao_p = 'NR') then
	ds_retorno_w := nr_consultor_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
