CREATE OR REPLACE FUNCTION proj_obter_dados_equipes ( nr_seq_equipe_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* function para obter informações da tabela PROJ_EQUIPE*/

ds_retorno_w		varchar(255);
nm_guerra_w		varchar(100);
ds_lista_nomes_w	varchar(255);

C01 CURSOR FOR
	SELECT	nm_guerra
	from	proj_equipe_papel
	where	nr_seq_equipe	= nr_seq_equipe_p
	and	(nm_guerra IS NOT NULL AND nm_guerra::text <> '')
	order by nm_guerra;


BEGIN

if (ie_opcao_p = 'NG') then
	open C01;
	loop
	fetch C01 into
		nm_guerra_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		ds_lista_nomes_w	:= ds_lista_nomes_w || nm_guerra_w || ' - ';
		end;
	end loop;
	close C01;

	ds_retorno_w	:= substr(ds_lista_nomes_w,1,length(ds_lista_nomes_w) -3);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
