CREATE OR REPLACE FUNCTION proj_obter_dados_equipe_papel ( nr_seq_equipe_papel_p bigint, ie_opcao_p text, cd_pessoa_fisica_p text default null) RETURNS varchar AS $body$
DECLARE

/* IE_OPCAO_P
	D - Pessoa/Funcao
	C - Codigo da Pessoa
	E - Email
*/
ds_retorno_w		varchar(255);
ds_pessoa_funcao_w	varchar(255);
cd_pessoa_fisica_w	varchar(10);
ds_email_w		proj_equipe_papel.ds_email%type;


BEGIN

select	coalesce(substr(obter_nome_pessoa_fisica(cd_pessoa_fisica,null),1,100),nm_pessoa) || ' (' || substr(proj_obter_dados_funcao(nr_seq_funcao,'T'),1,80) || ')',
	cd_pessoa_fisica
into STRICT	ds_pessoa_funcao_w,
	cd_pessoa_fisica_w
from	proj_equipe_papel
where	nr_sequencia	= nr_seq_equipe_papel_p;

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '' AND nr_seq_equipe_papel_p IS NOT NULL AND nr_seq_equipe_papel_p::text <> '') then
	select	max(ds_email)
	into STRICT	ds_email_w
	from	proj_equipe_papel
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	nr_seq_equipe = nr_seq_equipe_papel_p;
end if;

if (ie_opcao_p = 'D') then
	ds_retorno_w	:= ds_pessoa_funcao_w;
elsif (ie_opcao_p = 'C') then
	ds_retorno_w	:= cd_pessoa_fisica_w;
elsif (ie_opcao_p = 'E') then
	ds_retorno_w	:= ds_email_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
