CREATE OR REPLACE FUNCTION proj_obter_dados_etapa (nr_seq_cron_etapa_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/* ie_opcao_p
	A - Atividade
*/
ds_retorno_w		varchar(255);
ds_atividade_w		varchar(255);


BEGIN

select	ds_atividade
into STRICT	ds_atividade_w
from	proj_cron_etapa
where	nr_sequencia	= nr_seq_cron_etapa_p;

if (ie_opcao_p	= 'A') then
	ds_retorno_w	:= ds_atividade_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
