SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION proj_obter_dados_projeto ( nr_seq_projeto_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* ie_opcao_p
	T = Título
	C = Cliente
	E = Informação externa
	W = NOme Coordenador
	Y = Código do coordenador
	G = Nome da Gerência
	OS = Ordem de Serviço
*/
ds_retorno_w		varchar(255);
nm_coordenador_w	varchar(255);
cd_coordenador_w	varchar(255);
ds_titulo_w		varchar(255);
nm_cliente_w		varchar(80);
ds_informacao_externa_w	varchar(255);
ds_gerencia_w		varchar(80);
nr_seq_ordem_serv_w	proj_projeto.nr_seq_ordem_serv%type;


BEGIN

select	ds_titulo,
	substr(obter_desc_cliente(nr_seq_cliente,'F'),1,254) nm_cliente,
	ds_informacao_externa,
	substr(obter_nome_pf_pj(cd_coordenador, ''),1,254) nm_coordenador,
	substr(obter_descricao_gerencia(nr_seq_gerencia),1,80) ds_gerencia,
	nr_seq_ordem_serv
into STRICT	ds_titulo_w,
	nm_cliente_w,
	ds_informacao_externa_w,
	nm_coordenador_w,
	ds_gerencia_w,
	nr_seq_ordem_serv_w
from	proj_projeto
where	nr_sequencia	= nr_seq_projeto_p;

if (ie_opcao_p = 'T') then
	ds_retorno_w := ds_titulo_w;
elsif (ie_opcao_p = 'C') then
	ds_retorno_w	:= nm_cliente_w;
elsif (ie_opcao_p = 'E') then
	ds_retorno_w	:= ds_informacao_externa_w;
elsif (ie_opcao_p = 'W') then
	ds_retorno_w := nm_coordenador_w;
elsif (ie_opcao_p = 'Y') then
	ds_retorno_w := cd_coordenador_w;
elsif (ie_opcao_p = 'G') then
	ds_retorno_w := ds_gerencia_w;
elsif (ie_opcao_p = 'OS') then
	ds_retorno_w := nr_seq_ordem_serv_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
