SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION proj_obter_dados_roteiro ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(80);
ds_roteiro_w			varchar(80);
pr_grau_importancia_w	double precision;
nr_seq_modulo_w		bigint;

/* ie_opcao_p
D - Descrição Roteiro
G - Grau importância
M - Módulo
*/
BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	select	ds_roteiro,
		pr_grau_importancia,
		nr_seq_modulo
	into STRICT	ds_roteiro_w,
		pr_grau_importancia_w,
		nr_seq_modulo_w
	from	proj_tp_roteiro
	where	nr_sequencia = nr_sequencia_p;

	if (ie_opcao_p = 'D') then
		ds_retorno_w	:= ds_roteiro_w;
	elsif (ie_opcao_p = 'G') then
		ds_retorno_w	:= pr_grau_importancia_w;
	elsif (ie_opcao_p = 'M') then
		ds_retorno_w	:= nr_seq_modulo_w;
	end if;
end if;

RETURN	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
